import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Papa from "papaparse";

function Filters({ onFilterChange }) {
  const [sites, setSites] = useState([]);
  const [filteredSites, setFilteredSites] = useState([]);
  const [selectedSites, setSelectedSites] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  // Default to past two years
  const defaultStartDate = new Date();
  defaultStartDate.setFullYear(defaultStartDate.getFullYear() - 2);

  const [startDate, setStartDate] = useState(defaultStartDate);
  const [endDate, setEndDate] = useState(new Date());

  // New parameter filter
  const defaultParameter = "Total Phosphorous";
  const [parameter, setParameter] = useState(defaultParameter);

  // Load site data from CSV
  useEffect(() => {
    fetch("/locations.csv")
      .then((response) => response.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (result) => {
            const siteList = result.data
              .filter((site) => site.name)
              .map((site) => ({
                id: site.id,
                name: site.name.trim(),
                latitude: site.latitude,
                longitude: site.longitude,
              }));
            setSites(siteList);
            setFilteredSites(siteList);
          },
        });
      });
  }, []);

  // Filter site list by search query
  useEffect(() => {
    const filtered = sites.filter((site) =>
      site.name?.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredSites(filtered);
  }, [searchQuery, sites]);

  // Notify parent of any filter changes
  useEffect(() => {
    onFilterChange({
      selectedSites,
      parameter,
      startDate,
      endDate,
    });
  }, [selectedSites, parameter, startDate, endDate, onFilterChange]);

  // Toggle a site in/out of the selectedSites array
  const handleSiteClick = (site) => {
    setSelectedSites((prev) => {
      const alreadySelected = prev.some((s) => s.id === site.id);
      return alreadySelected
        ? prev.filter((s) => s.id !== site.id)
        : [...prev, site];
    });
  };

  // Reset all filters
  const resetFilters = () => {
    setSearchQuery("");
    setFilteredSites(sites);
    setSelectedSites([]);
    setStartDate(defaultStartDate);
    setEndDate(new Date());
    setParameter(defaultParameter); // Reset parameter
  };

  return (
    <div className="filters">
      <h3>Filter Data</h3>

      {/* Search Box */}
      <input
        type="text"
        placeholder="Search sites..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-box"
      />

      {/* Scrollable Site List - Multiple Selection */}
      <div className="site-list">
        {filteredSites.map((site) => {
          const isSelected = selectedSites.some((s) => s.id === site.id);
          return (
            <div
              key={site.id}
              className={`site-item ${isSelected ? "selected" : ""}`}
              onClick={() => handleSiteClick(site)}
            >
              {site.name}
            </div>
          );
        })}
      </div>

      {/* Parameter Filter */}
      <label>Parameter:</label>
      <select
        className="parameter-select"
        value={parameter}
        onChange={(e) => setParameter(e.target.value)}
      >
        <option value="Total Phosphorous">Total Phosphorous</option>
        <option value="Secchi">Secchi</option>
      </select>

      {/* Date Range */}
      <h4>Select a date range</h4>
      <label>From:</label>
      <DatePicker
        selected={startDate}
        onChange={(date) => setStartDate(date)}
        selectsStart
        startDate={startDate}
        endDate={endDate}
        dateFormat="MM/dd/yyyy"
        placeholderText="MM/dd/yyyy"
        showPopperArrow
        className="date-picker"
      />

      <label>Until:</label>
      <DatePicker
        selected={endDate}
        onChange={(date) => setEndDate(date)}
        selectsEnd
        startDate={startDate}
        endDate={endDate}
        minDate={startDate}
        dateFormat="MM/dd/yyyy"
        placeholderText="MM/dd/yyyy"
        showPopperArrow
        className="date-picker"
      />

      {/* Buttons */}
      <div className="filter-buttons">
        <button onClick={resetFilters} className="reset-btn">Reset</button>
        <button onClick={() => setEndDate(new Date())} className="today-btn">Today</button>
      </div>
    </div>
  );
}

export default Filters;
