import React, { useEffect, useState } from "react";
import Papa from "papaparse";

function Data() {
  // List of available CSV files in the public folder
  const csvFiles = ["data.csv", "users.csv", "metrics.csv"];
  const [selectedFile, setSelectedFile] = useState(csvFiles[0]);
  const [csvData, setCsvData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Function to load CSV file from public folder
  const loadCSV = (fileName) => {
    setLoading(true);
    fetch(`/${fileName}`)
      .then((response) => response.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          complete: (result) => {
            setCsvData(result.data);
            setLoading(false);
          },
        });
      })
      .catch((error) => {
        console.error("Error loading CSV:", error);
        setLoading(false);
      });
  };

  // Load CSV whenever the selected file changes
  useEffect(() => {
    loadCSV(selectedFile);
  }, [selectedFile]);

  // Handle changes in CSV data cells
  const handleChange = (rowIndex, field, newValue) => {
    const updatedData = [...csvData];
    updatedData[rowIndex][field] = newValue;
    setCsvData(updatedData);
  };

  // Add new row: use headers from the first row if available
  const addNewRow = () => {
    let newRow = {};
    if (csvData.length > 0) {
      const headers = Object.keys(csvData[0]);
      headers.forEach((header) => {
        newRow[header] = "";
      });
    }
    setCsvData([...csvData, newRow]);
  };

  // Commit save function: simulate saving by downloading the updated CSV file
  const commitSave = () => {
    const csv = Papa.unparse(csvData);
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    const updatedFileName = selectedFile.replace(".csv", "_updated.csv");
    link.download = updatedFileName;
    link.click();
    // In a real application, you might send the updated data to a backend server here.
  };

  if (loading) return <div>Loading CSV data...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Data Editor</h2>
      {/* CSV File Selection */}
      <div style={{ marginBottom: "15px" }}>
        <label htmlFor="csv-select">Select CSV File: </label>
        <select
          id="csv-select"
          value={selectedFile}
          onChange={(e) => setSelectedFile(e.target.value)}
        >
          {csvFiles.map((file, idx) => (
            <option key={idx} value={file}>
              {file}
            </option>
          ))}
        </select>
      </div>
      {/* Add New Row Button */}
      <div style={{ marginBottom: "15px" }}>
        <button onClick={addNewRow}>Add New Row</button>
      </div>
      {/* Editable CSV Data Table */}
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            {csvData.length > 0 &&
              Object.keys(csvData[0]).map((header, index) => (
                <th key={index}>{header}</th>
              ))}
          </tr>
        </thead>
        <tbody>
          {csvData.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {Object.entries(row).map(([field, value], colIndex) => (
                <td key={colIndex}>
                  <input
                    type="text"
                    value={value}
                    onChange={(e) =>
                      handleChange(rowIndex, field, e.target.value)
                    }
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <br />
      {/* Commit Save Button */}
      <button onClick={commitSave}>Commit Save</button>
    </div>
  );
}

export default Data;
