import React, { useEffect, useState } from "react";
import Papa from "papaparse";

function Filters({ onFilterChange }) {
  const [sites, setSites] = useState([]);
  const [selectedSites, setSelectedSites] = useState([]);
  const [selectedParameters, setSelectedParameters] = useState([]);
  const [availableYears, setAvailableYears] = useState([]);
  const [startYear, setStartYear] = useState(null);
  const [endYear, setEndYear] = useState(null);

  // Load site locations & determine available years from CSV
  useEffect(() => {
    fetch("/water_quality_data.csv")
      .then(response => response.text())
      .then(csvText => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (result) => {
            processCSVData(result.data);
          }
        });
      });
  }, []);

  const processCSVData = (data) => {
    // Extract unique sites
    const uniqueSites = [...new Set(data.map(row => row.Location))];
    setSites(uniqueSites);

    // Extract and sort unique years from CSV
    const years = [...new Set(data.map(row => parseInt(row.Year)))].sort((a, b) => a - b);
    
    setAvailableYears(years);
    setStartYear(years[0]);
    setEndYear(years[years.length - 1]);

    // Update filters with default values
    onFilterChange(prev => ({
      ...prev,
      startDate: new Date(years[0], 0, 1),
      endDate: new Date(years[years.length - 1], 11, 31),
    }));
  };

  // Handle start year change
  const handleStartYearChange = (event) => {
    const newStartYear = parseInt(event.target.value);
    if (newStartYear <= endYear) {
      setStartYear(newStartYear);
      onFilterChange(prev => ({
        ...prev,
        startDate: new Date(newStartYear, 0, 1),
      }));
    }
  };

  // Handle end year change
  const handleEndYearChange = (event) => {
    const newEndYear = parseInt(event.target.value);
    if (newEndYear >= startYear) {
      setEndYear(newEndYear);
      onFilterChange(prev => ({
        ...prev,
        endDate: new Date(newEndYear, 11, 31),
      }));
    }
  };

  return (
    <div className="filters">
      
      {/* Site Filter */}
      <div className="filter-group">
        <input 
          type="text" 
          placeholder="Search sites..." 
          className="search-box"
          onChange={(e) => {
            const query = e.target.value.toLowerCase();
            setSites(prevSites => prevSites.filter(site => site.toLowerCase().includes(query)));
          }} 
        />
        <div className="site-list">
          {sites.map(site => (
            <div 
              key={site} 
              className={`site-item ${selectedSites.includes(site) ? "selected" : ""}`}
              onClick={() => {
                const updatedSelection = selectedSites.includes(site)
                  ? selectedSites.filter(s => s !== site)
                  : [...selectedSites, site];
                setSelectedSites(updatedSelection);
                onFilterChange(prev => ({ ...prev, selectedSites: updatedSelection }));
              }}
            >
              {site}
            </div>
          ))}
        </div>
      </div>

      <hr className="filter-separator" />

      {/* Parameter Filter */}
      <div className="filter-group">
        <span>Parameter:</span>
        <div className="parameter-options">
          {["Total Phosphorous", "Secchi"].map(param => (
            <label key={param} className="parameter-option">
              <input 
                type="checkbox" 
                checked={selectedParameters.includes(param)}
                onChange={() => {
                  const updatedParams = selectedParameters.includes(param)
                    ? selectedParameters.filter(p => p !== param)
                    : [...selectedParameters, param];
                  setSelectedParameters(updatedParams);
                  onFilterChange(prev => ({ ...prev, selectedParameters: updatedParams }));
                }}
              />
              {param}
            </label>
          ))}
        </div>
      </div>

      <hr className="filter-separator" />

      {/* Year Range Dropdowns */}
      <div className="filter-group">
        <span>Year Range:</span>
        <div className="year-dropdowns">
          <label>
            Start Year:
            <select value={startYear || ""} onChange={handleStartYearChange} className="year-select">
              {availableYears.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </label>
          <label>
            End Year:
            <select value={endYear || ""} onChange={handleEndYearChange} className="year-select">
              {availableYears.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </label>
        </div>
      </div>

      <hr className="filter-separator" />

      {/* Reset Button */}
      <div className="filter-group filter-buttons">
        <button className="reset-btn" onClick={() => {
          setSelectedSites([]);
          setSelectedParameters([]);
          setStartYear(availableYears[0]);
          setEndYear(availableYears[availableYears.length - 1]);
          onFilterChange({
            selectedSites: [],
            selectedParameters: [],
            startDate: new Date(availableYears[0], 0, 1),
            endDate: new Date(availableYears[availableYears.length - 1], 11, 31),
          });
        }}>
          Reset
        </button>
      </div>

    </div>
  );
}

export default Filters;
