import React, { useEffect, useState } from "react";
import Papa from "papaparse";

function Filters({ onFilterChange }) {
  const [sites, setSites] = useState([]);
  const [selectedSites, setSelectedSites] = useState([]);
  const [selectedParameters, setSelectedParameters] = useState([]);
  const [yearRange, setYearRange] = useState([2000, 2025]); // Default range
  const [minYear, setMinYear] = useState(2000);
  const [maxYear, setMaxYear] = useState(2025);

  // Load site locations & determine available years from CSV
  useEffect(() => {
    fetch("/water_quality_data.csv")
      .then(response => response.text())
      .then(csvText => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (result) => {
            processCSVData(result.data);
          }
        });
      });
  }, []);

  const processCSVData = (data) => {
    // Extract unique sites
    const uniqueSites = [...new Set(data.map(row => row.Location))];
    setSites(uniqueSites);

    // Determine min and max year in dataset
    const years = data.map(row => parseInt(row.Year));
    const min = Math.min(...years);
    const max = Math.max(...years);

    setMinYear(min);
    setMaxYear(max);
    setYearRange([min, max]); // Set default slider range
  };

  // Handle year range change
  const handleYearChange = (event, index) => {
    const newYearRange = [...yearRange];
    newYearRange[index] = Number(event.target.value);
    setYearRange(newYearRange);

    // Update App.js state
    onFilterChange(prev => ({
      ...prev,
      startDate: new Date(newYearRange[0], 0, 1),
      endDate: new Date(newYearRange[1], 11, 31),
    }));
  };

  return (
    <div className="filters">
      
      {/* Site Filter */}
      <div className="filter-group">
        <input 
          type="text" 
          placeholder="Search sites..." 
          className="search-box"
          onChange={(e) => {
            const query = e.target.value.toLowerCase();
            setSites(prevSites => prevSites.filter(site => site.toLowerCase().includes(query)));
          }} 
        />
        <div className="site-list">
          {sites.map(site => (
            <div 
              key={site} 
              className={`site-item ${selectedSites.includes(site) ? "selected" : ""}`}
              onClick={() => {
                const updatedSelection = selectedSites.includes(site)
                  ? selectedSites.filter(s => s !== site)
                  : [...selectedSites, site];
                setSelectedSites(updatedSelection);
                onFilterChange(prev => ({ ...prev, selectedSites: updatedSelection }));
              }}
            >
              {site}
            </div>
          ))}
        </div>
      </div>

      <hr className="filter-separator" />

      {/* Parameter Filter */}
      <div className="filter-group">
        <span>Parameter:</span>
        <div className="parameter-options">
          {["Total Phosphorous", "Secchi"].map(param => (
            <label key={param} className="parameter-option">
              <input 
                type="checkbox" 
                checked={selectedParameters.includes(param)}
                onChange={() => {
                  const updatedParams = selectedParameters.includes(param)
                    ? selectedParameters.filter(p => p !== param)
                    : [...selectedParameters, param];
                  setSelectedParameters(updatedParams);
                  onFilterChange(prev => ({ ...prev, selectedParameters: updatedParams }));
                }}
              />
              {param}
            </label>
          ))}
        </div>
      </div>

      <hr className="filter-separator" />

      {/* Year Range Slider */}
      <div className="filter-group year-slider-container">
        <span>Year Range:</span>
        <div className="year-slider-wrapper">
          <input 
            type="range" 
            min={minYear} 
            max={maxYear} 
            value={yearRange[0]} 
            step="1"
            onChange={(event) => handleYearChange(event, 0)} 
            className="year-slider"
          />
          <input 
            type="range" 
            min={minYear} 
            max={maxYear} 
            value={yearRange[1]} 
            step="1"
            onChange={(event) => handleYearChange(event, 1)} 
            className="year-slider"
          />
        </div>
        <div className="year-values">
          <span>{yearRange[0]}</span>
          <span>{yearRange[1]}</span>
        </div>
      </div>

      <hr className="filter-separator" />

      {/* Reset Button */}
      <div className="filter-group filter-buttons">
        <button className="reset-btn" onClick={() => {
          setSelectedSites([]);
          setSelectedParameters([]);
          setYearRange([minYear, maxYear]);
          onFilterChange({
            selectedSites: [],
            selectedParameters: [],
            startDate: new Date(minYear, 0, 1),
            endDate: new Date(maxYear, 11, 31),
          });
        }}>
          Reset
        </button>
      </div>

    </div>
  );
}

export default Filters;
