import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Papa from "papaparse";

function Filters({ onFilterChange }) {
  const [sites, setSites] = useState([]);
  const [filteredSites, setFilteredSites] = useState([]);
  const [selectedSites, setSelectedSites] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  // Default to past two years
  const defaultStartDate = new Date();
  defaultStartDate.setFullYear(defaultStartDate.getFullYear() - 2);
  const [startDate, setStartDate] = useState(defaultStartDate);
  const [endDate, setEndDate] = useState(new Date());

  // Parameter options (multiple selection allowed)
  const parameterOptions = ["Total Phosphorous", "Secchi"];
  const [selectedParameters, setSelectedParameters] = useState([...parameterOptions]); // default to both

  // Load site data from CSV (from /locations.csv)
  useEffect(() => {
    fetch("/locations.csv")
      .then((response) => response.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (result) => {
            const siteList = result.data
              .filter((site) => site.name)
              .map((site) => ({
                id: site.id,
                name: site.name.trim(),
                latitude: site.latitude,
                longitude: site.longitude,
              }));
            setSites(siteList);
            setFilteredSites(siteList);
          },
        });
      });
  }, []);

  // Update site list based on search query
  useEffect(() => {
    const filtered = sites.filter((site) =>
      site.name?.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredSites(filtered);
  }, [searchQuery, sites]);

  // Notify parent of filter changes
  useEffect(() => {
    onFilterChange({
      selectedSites,
      selectedParameters,
      startDate,
      endDate,
    });
  }, [selectedSites, selectedParameters, startDate, endDate, onFilterChange]);

  // Toggle a site in/out of the selectedSites array
  const handleSiteClick = (site) => {
    setSelectedSites((prev) => {
      const alreadySelected = prev.some((s) => s.id === site.id);
      return alreadySelected
        ? prev.filter((s) => s.id !== site.id)
        : [...prev, site];
    });
  };

  // Toggle parameter selection for multi-select
  const handleParameterChange = (parameter) => {
    setSelectedParameters((prev) => {
      if (prev.includes(parameter)) {
        return prev.filter((p) => p !== parameter);
      } else {
        return [...prev, parameter];
      }
    });
  };

  // Reset all filters
  const resetFilters = () => {
    setSearchQuery("");
    setFilteredSites(sites);
    setSelectedSites([]);
    setStartDate(defaultStartDate);
    setEndDate(new Date());
    setSelectedParameters([...parameterOptions]);
  };

  return (
    <div className="filters">

      {/* Search Box */}
      <input
        type="text"
        placeholder="Search sites..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-box"
      />

      {/* Scrollable Site List - Multiple Selection */}
      <div className="site-list">
        {filteredSites.map((site) => {
          const isSelected = selectedSites.some((s) => s.id === site.id);
          return (
            <div
              key={site.id}
              className={`site-item ${isSelected ? "selected" : ""}`}
              onClick={() => handleSiteClick(site)}
            >
              {site.name}
            </div>
          );
        })}
      </div>

      {/* Multi-Select Parameter Filter */}
      <div className="parameter-filter">
        <label>Parameter:</label>
        <div className="parameter-options">
          {parameterOptions.map((parameter) => (
            <div key={parameter} className="parameter-option">
              <input
                type="checkbox"
                id={parameter}
                checked={selectedParameters.includes(parameter)}
                onChange={() => handleParameterChange(parameter)}
              />
              <label htmlFor={parameter}>{parameter}</label>
            </div>
          ))}
        </div>
      </div>

      {/* Date Range */}
      
      <label>From:</label>
      <DatePicker
        selected={startDate}
        onChange={(date) => setStartDate(date)}
        selectsStart
        startDate={startDate}
        endDate={endDate}
        dateFormat="MM/dd/yyyy"
        placeholderText="MM/dd/yyyy"
        showPopperArrow
        className="date-picker"
      />

      <label>Until:</label>
      <DatePicker
        selected={endDate}
        onChange={(date) => setEndDate(date)}
        selectsEnd
        startDate={startDate}
        endDate={endDate}
        minDate={startDate}
        dateFormat="MM/dd/yyyy"
        placeholderText="MM/dd/yyyy"
        showPopperArrow
        className="date-picker"
      />

      {/* Buttons */}
      <div className="filter-buttons">
        <button onClick={resetFilters} className="reset-btn">
          Reset
        </button>
        <button onClick={() => setEndDate(new Date())} className="today-btn">
          Today
        </button>
      </div>
    </div>
  );
}

export default Filters;
